const AddCategory=()=>{
    const [name,setName]=useState("initiastate");
    const [error,setError]=useState(false);
    const [success,setSuccess]=useState(false);
    const {user,token} =isAuthenticated();

    const myCategoryForm=()=>(
        <form>
            <div className="form-group">
                <p className="lead">enter the category</p>
                <input className="form-control py-3"
                 type="text"
                 autoFocus
                 required
                 placeholder="for eg. summer"
                 />
                 <button className="btn btn-outline-info">create category</button>
            </div>
        </form>
        )
    const goBack=()=>(
        <div className="mt-5">
            <Link className="btn btn-sm btn-dark mb-3" to="/admin/dashboard">
                admin home
            </Link>
        </div>
    )



    return(
        <Base title="create a category"
        className="container bg-info p-4"
        description="add a new category">
            <div className="row bg-white rounded offset-md-2">
                {myCategoryForm()}
                {goBack()}
            </div>

        </Base>
        )
}

export default AddCategory;
